local takeJob = {x=-1905.18,y=-570.54,z=19.09}
local bossUI = {x=-1912.0642,y=-569.76,z=19.09}

ESX                             = nil
local PlayerData = {}

Citizen.CreateThread(function()
  while ESX == nil do
    TriggerEvent('esx:getSharedObject', function(obj) ESX = obj end)
    Citizen.Wait(0)
  end
end)

RegisterNetEvent('esx_phone:loaded')
AddEventHandler('esx_phone:loaded', function(phoneNumber, contacts)
	local specialContact = {
		name       = 'Avocat',
		number     = 'avocat',
		base64Icon = 'data:image/png;base64,'
	}
	

	TriggerEvent('esx_phone:addSpecialContact', specialContact.name, specialContact.number, specialContact.base64Icon)

end)

RegisterNetEvent('esx:playerLoaded')
AddEventHandler('esx:playerLoaded', function(xPlayer)
	PlayerData = xPlayer
end)



RegisterNetEvent('esx:setJob')
AddEventHandler('esx:setJob', function(job)
	PlayerData.job = job
end)


Citizen.CreateThread(function()
	while true do
		Citizen.Wait(0)
		if(PlayerData.job ~= nil and PlayerData.job.name == "avocat") then
			if(GetDistanceBetweenCoords(takeJob.x,takeJob.y,takeJob.z,  GetEntityCoords(GetPlayerPed(-1)), true) < 3) then 
				Info("Appuyer sur ~g~E~w~ pour vous changer.")
				DrawMarker(1,takeJob.x,takeJob.y,takeJob.z-1,0,0,0,0,0,0,2.001,2.0001,0.5001,0,155,255,200,0,0,0,0)
				if(IsControlJustPressed(1, 38)) then
					openTakeService()
				end
			end
		end

		if(PlayerData.job ~= nil and PlayerData.job.name == "avocat" and PlayerData.job.grade_name == "boss") then
			if(GetDistanceBetweenCoords(bossUI.x,bossUI.y,bossUI.z,  GetEntityCoords(GetPlayerPed(-1)), true) < 3) then 
				Info("Appuyer sur ~g~E~w~ pour ouvrir le menu patron.")
				DrawMarker(1,bossUI.x,bossUI.y,bossUI.z-1,0,0,0,0,0,0,2.001,2.0001,0.5001,0,155,255,200,0,0,0,0)
				if(IsControlJustPressed(1, 38)) then
					TriggerEvent('esx_society:openBossMenu', 'avocat', function(data, menu)
						menu.close()
					end)
				end
			end
		end
	end
end)


function Info(text, loop)
	SetTextComponentFormat("STRING")
	AddTextComponentString(text)
	DisplayHelpTextFromStringLabel(0, loop, 1, 0)
end



function openTakeService()

  local elements = {
    {label = "citizen wear", value = 'citizen_wear'},
    {label = "lawyer wear", value = 'avocat_wear'}
  }

  ESX.UI.Menu.CloseAll()

    ESX.UI.Menu.Open(
      'default', GetCurrentResourceName(), 'cloakroom',
      {
        title    = 'Service plug',
        align    = 'bottom-right',
        elements = elements,
        },

        function(data, menu)

      menu.close()

      --Taken from SuperCoolNinja
      if data.current.value == 'citizen_wear' then
        ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin, jobSkin)
          local model = nil

          if skin.sex == 0 then
            model = GetHashKey("mp_m_freemode_01")
          else
            model = GetHashKey("mp_f_freemode_01")
          end

          RequestModel(model)
          while not HasModelLoaded(model) do
            RequestModel(model)
            Citizen.Wait(1)
          end

          SetPlayerModel(PlayerId(), model)
          SetModelAsNoLongerNeeded(model)

          TriggerEvent('skinchanger:loadSkin', skin)
          TriggerEvent('esx:restoreLoadout')
        end)
      end

      if data.current.value == 'avocat_wear' then
		TriggerEvent('skinchanger:getSkin', function(skin)
			local clothesSkin = {
				['bags_1'] = 0, ['bags_2'] = 0,
				['tshirt_1'] = 13, ['tshirt_2'] = 0,
				['torso_1'] = 4, ['torso_2'] = 0,
				['arms'] = 1,
				['pants_1'] = 28, ['pants_2'] = 0,
				['shoes_1'] = 20, ['shoes_2'] = 1,
				['mask_1'] = 0, ['mask_2'] = 0,
				['bproof_1'] = 0,
				['chain_1'] = 10, ['chain_2'] = 0,
				['helmet_1'] = -1, ['helmet_2'] = 0
			}
			TriggerEvent('skinchanger:loadClothes', skin, clothesSkin)
		end)

      end


    end,
    function(data, menu)

      menu.close()

    end
  )

end



Citizen.CreateThread(function()
	local blip = AddBlipForCoord(-1898.59, -559.90, 11.76)

	SetBlipSprite (blip, 525)
	SetBlipDisplay(blip, 4)
	SetBlipScale  (blip, 0.85)
	SetBlipColour (blip, 11)
	SetBlipAsShortRange(blip, true)

	BeginTextCommandSetBlipName('STRING')
	AddTextComponentSubstringPlayerName('Cabinet Avocat')
	EndTextCommandSetBlipName(blip)
end)